   console.log(results);
   setDataSource(results);

  }, [tech, address]);*/
  // type dataT = {
  //   address: string;
  // }

  useEffect(()=>{
    axios
    .get("./fake/address.json")
    .then((res)=>{
      setDDAdress(res.data);
      console.log(res.data);
    })
    .catch((error)=>{
      console.log(error);
    });

  },[]);


  // useEffect(()=>{
  //   axios
  //   .get("./fake/person.json")
  //   .then((res)=>{
  //     // setDDAdress(res.data);
  //     setData(res.data);
